import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";
import Axios from "axios";

export default function Login() {
  const handleCadastro = async (values: any) => {
    Axios.post("https://localhost:3001/register", {
      email: values.email,
      password: values.password,
    }).then((response) => {
      console.log(response);
    });
  };
  const handleClick = async (values: any) => {
    Axios.post("https://localhost:3001/login", {
      email: values.email,
      password: values.password,
    }).then((response) => {
      console.log(response);
    });
  };
  const validationLogin = yup.object().shape({
    email: yup
      .string()
      .email("Email inválido")
      .required("Este campo é obrigatório"),
    password: yup
      .string()
      .min(8, "O nome deve ter no mínimo 8 caracteres")
      .required("Este campo é obrigatório"),
  });
  const validationCadastrar = yup.object().shape({
    email: yup
      .string()
      .email("Email inválido")
      .required("Este campo é obrigatório"),
    password: yup
      .string()
      .min(8, "O nome deve ter no mínimo 8 caracteres")
      .required("Este campo é obrigatório"),
    passwordConfirm: yup
      .string()
      .oneOf([yup.ref("password")], "As senhas não são indênticas"),
  });
  return (
    <>
      <h2>Login</h2>
      <Formik
        initialValues={{}}
        onSubmit={async () => {
          await handleClick;
        }}
        validationSchema={validationLogin}
      >
        <Form>
          <div>
            <Field
              name="email"
              className="form-field"
              placeholder="email"
            ></Field>
            <ErrorMessage
              component="span"
              name="email"
              className="form-error"
            ></ErrorMessage>
            <Field
              name="password"
              className="form-field"
              placeholder="password"
            ></Field>
            <ErrorMessage
              component="span"
              name="password"
              className="form-error"
            ></ErrorMessage>
            <button className="button" type="submit">
              Login
            </button>
          </div>
        </Form>
      </Formik>
      <h2>Cadastro</h2>
      <Formik
        initialValues={{}}
        onSubmit={async () => {
          await handleCadastro;
        }}
        validationSchema={validationLogin}
      >
        <Form>
          <div>
            <Field
              name="email"
              className="form-field"
              placeholder="email"
            ></Field>
            <ErrorMessage
              component="span"
              name="email"
              className="form-error"
            ></ErrorMessage>
            <Field
              name="password"
              className="form-field"
              placeholder="password"
            ></Field>
            <ErrorMessage
              component="span"
              name="password"
              className="form-error"
            ></ErrorMessage>
            <Field
              name="passwordConfirm"
              className="form-field"
              placeholder="Confirme sua senha"
            ></Field>
            <ErrorMessage
              component="span"
              name="passwordConfirm"
              className="form-error"
            ></ErrorMessage>
            <button className="button" onSubmit={() => {
              
            }} type="submit">
              Cadastrar
            </button>
          </div>
        </Form>
      </Formik>
    </>
  );
}
