import Axios from "axios";
import { ErrorMessage, Field, Form, Formik } from "formik";
const yup = require("yup");
export default function Cadastrar() {
  const handleCadastro = async (values: any) => {
    Axios.post("http://localhost:3001/register", {
      email: values.email,
      password: values.password,
    }).then((response) => {
      alert(response.data.msg);
    });
  };
  const validationCadastrar = yup.object().shape({
    email: yup
      .string()
      .email("Email inválido")
      .required("Este campo é obrigatório"),
    password: yup
      .string()
      .min(8, "O nome deve ter no mínimo 8 caracteres")
      .required("Este campo é obrigatório"),
    passwordConfirm: yup
      .string()
      .oneOf([yup.ref("password")], "As senhas não são indênticas"),
  });
  return (
    <>
      <h2>Cadastro</h2>
      <Formik
        initialValues={{}}
        onSubmit={(values) => {
          handleCadastro(values);
        }}
        validationSchema={validationCadastrar}
      >
        <Form>
          <div>
            <Field
              name="email"
              className="form-field"
              placeholder="Email"
            ></Field>
            <ErrorMessage
              component="span"
              name="email"
              className="form-error"
            ></ErrorMessage>
            <Field
              type="password"
              name="password"
              className="form-field"
              placeholder="Senha"
            ></Field>
            <ErrorMessage
              component="span"
              name="password"
              className="form-error"
            ></ErrorMessage>
            <Field
              name="passwordConfirm"
              className="form-field"
              placeholder="Confirme sua senha"
            ></Field>
            <ErrorMessage
              component="span"
              name="passwordConfirm"
              className="form-error"
            ></ErrorMessage>
            <button className="button" type="submit">
              Cadastrar
            </button>
          </div>
        </Form>
      </Formik>
    </>
  );
}
