import Axios from "axios";
import { ErrorMessage, Field, Form, Formik } from "formik";
import styles from "./Cadastrar.module.scss";
const yup = require("yup");
export default function Cadastrar() {
  const handleCadastro = async (values: any) => {
    Axios.post("http://localhost:3001/register", {
      email: values.email,
      password: values.password,
    }).then((response) => {
      alert(response.data.msg);
    });
  };
  const validationCadastrar = yup.object().shape({
    email: yup
      .string()
      .email("Email inválido")
      .required("Este campo é obrigatório"),
    password: yup
      .string()
      .min(8, "O nome deve ter no mínimo 8 caracteres")
      .required("Este campo é obrigatório"),
    passwordConfirm: yup
      .string()
      .oneOf([yup.ref("password")], "As senhas não são indênticas"),
  });
  return (
    <>
      <div className={styles.cadastrar}>
        <h2 className={styles["cadastrar__titulo"]}>Cadastro</h2>
        <Formik
          initialValues={{}}
          onSubmit={(values) => {
            handleCadastro(values);
          }}
          validationSchema={validationCadastrar}
        >
          <Form>
            <div className={styles['cadastrar__form']}>
              <Field
                name="name"
                className={styles["cadastrar__input"]}
                placeholder="Nome"
              ></Field>
              <ErrorMessage
                component="span"
                name="name"
                className={styles["cadastrar__error"]}
              ></ErrorMessage>
              <Field
                name="date"
                className={styles["cadastrar__input"]}
                placeholder="Data de nascimento"
              ></Field>
              <ErrorMessage
                component="span"
                name="name"
                className={styles["cadastrar__error"]}
              ></ErrorMessage>
              <Field
                name="email"
                className={styles["cadastrar__input"]}
                placeholder="Email"
              ></Field>
              <ErrorMessage
                component="span"
                name="email"
                className={styles["cadastrar__error"]}
              ></ErrorMessage>
              <Field
                type="password"
                name="password"
                className={styles["cadastrar__input"]}
                placeholder="Senha"
              ></Field>
              <ErrorMessage
                component="span"
                name="password"
                className={styles["cadastrar__error"]}
              ></ErrorMessage>
              <Field
                name="passwordConfirm"
                type='password'
                className={styles["cadastrar__input"]}
                placeholder="Confirme sua senha"
              ></Field>
              <ErrorMessage
                component="span"
                name="passwordConfirm"
                className={styles["cadastrar__error"]}
              ></ErrorMessage>
            </div>
            <button className="button" type="submit">
              Cadastrar
            </button>
          </Form>
        </Formik>
      </div>
    </>
  );
}
